import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function StudentExamResult() {
  const { studentId, examId } = useParams();
  const [result, setResult] = useState({});
  const [exam, setExam] = useState({});
  useEffect(() => {
    const fetchResult = async () => {
      const response = await fetch(`http://localhost:5001/api/view-result/${examId}/${studentId}`);
      if (response.status === 404) {
        setResult({ message: 'Result not published' });
        return;
      }
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const resultData = await response.json();
      setResult(resultData);
    };

    fetchResult();
  }, [examId, studentId]);


  useEffect(() => {
    const fetchExam = async () => {
      const response = await fetch(`http://localhost:5001/api/studentexam/${examId}`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const examData = await response.json();
      setExam(examData);
    };

    fetchExam();
  }, [examId]);

  return (
    <div>
      {result.message ? (
        <p>{result.message}</p>
      ) : (
        <>
          <h2>Exam Details:</h2>
          <p>Name: {exam?.name}</p>
          <p>Duration: {result.duration}</p>
          <p>Total Marks: {result.totalMarks}</p>
          <p>Subject: {result.subject}</p>
        </>
      )}
    </div>
  );
}
export default StudentExamResult;
