import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function StudentExamResult() {
  const { studentId, examId } = useParams();
  const [result, setResult] = useState({});
  const [exam, setExam] = useState({});

  useEffect(() => {
    const fetchResult = async () => {
      const response = await fetch(`http://localhost:5001/api/view-result/${examId}/${studentId}`);
      if (response.status === 404) {
        setResult({ message: 'Result not published' });
        return;
      }
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const resultData = await response.json();
      setResult(resultData);
    };

    fetchResult();
  }, [examId, studentId]);


  useEffect(() => {
    const fetchExam = async () => {
      const response = await fetch(`http://localhost:5001/api/student-exam/${examId}`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const examData = await response.json();
      setExam(examData);
    };

    fetchExam();
  }, [examId]);


  useEffect(() => {
    const fetchQuestions = async () => {
      const promises = exam?.questionIds.map(async (questionId) => {
        const response = await fetch(`http://localhost:5001/api/question/${questionId}`);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      });

      const questionsData = await Promise.all(promises);
      setQuestions(questionsData);
    };

    if (exam) {
      fetchQuestions();
    }
  }, [exam]);

  return (
    <div>
      {result.message ? (
        <p>{result.message}</p>
      ) : (
        <>
          <h2>Exam Details:</h2>
          <p>Name: {exam?.name}</p>
          <p>Duration: {exam?.duration}</p>
          <p>Total Marks: {exam?.totalMarks}</p>
          <p>Subject: {exam?.subject}</p>
          {questions.map((question, index) => (
            <div key={index}>
              <p>Question: {question.text}</p>
              <p>Correct Answer: {question.correctAnswer}</p>
              <p>Marks: {question.marks}</p>
            </div>
          ))}
        </>
      )}
    </div>
  );
}
export default StudentExamResult;
