import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

// ...

const AdminSubmission = () => {
    const { examId, studentId } = useParams();
    const [exam, setExam] = useState(null);
    const [questionTexts, setQuestionTexts] = useState([]);
    const [answerTexts, setAnswerTexts] = useState([]);
    const [marks, setMarks] = useState('');

    useEffect(() => {
        const fetchExamDetails = async () => {
            try {
                const response = await fetch(`http://localhost:5001/api/admin-view-exams/${examId}`);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const exam = await response.json();
                setExam(exam);

                // Fetch question text for each question ID
                const questionTexts = await Promise.all(exam.questionIds.map(async id => {
                    const response = await fetch(`http://localhost:5001/api/admin-view-question/${id}`);
                    if (!response.ok) {
                        throw new Error(`HTTP error! status: ${response.status}`);
                    }
                    const question = await response.json();
                    return question.questionText;
                }));

                setQuestionTexts(questionTexts);
                // Fetch answer text for each question ID and student ID
                const answerTexts = await Promise.all(exam.questionIds.map(async id => {
                    const response = await fetch(`http://localhost:5001/api/admin-view-answer/${id}/${studentId}`);
                    if (!response.ok) {
                        throw new Error(`HTTP error! status: ${response.status}`);
                    }
                    const answer = await response.json();
                    return answer.answerText;
                }));

                setAnswerTexts(answerTexts);
            } catch (error) {
                console.error(error);
            }
        };

        fetchExamDetails();
    }, [examId]);

    useEffect(() => {
        const fetchMarks = async () => {
            try {
                // Fetch marks for the student ID and exam ID
                const response = await fetch(`http://localhost:5001/api/admin-view-marks/${studentId}/${examId}`);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const marks = await response.json();
                setMarks(marks);
            } catch (error) {
                console.error(error);
            }
        };

        fetchMarks();
    }, [studentId, examId]);

    const submitMarks = async () => {
        try {
            const response = await fetch(`http://localhost:5001/api/admin-submit-marks/${studentId}/${examId}`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ marks }),
            });
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            alert("Marks submitted successfully!");
        } catch (error) {
            console.error(error);
        }
    };


    return (
        <div>
            {exam && (
                <>
                    <h2>Exam Details:</h2>
                    <p>Name: {exam.name}</p>
                    <p>Duration: {exam.duration}</p>
                    <p>Total Marks: {exam.totalMarks}</p>
                    <p> Marks Obtained: {typeof marks === 'string' ? marks : marks.marks}</p>
                    <h2>Question IDs and Texts:</h2>
                    {exam.questionIds.map((id, index) => (
                        <div key={index}>
                            <p>{questionTexts[index]}</p>
                            <p>Answer: {answerTexts[index]}</p>
                        </div>
                    ))}
                    <input type="number" value={marks} onChange={e => setMarks(e.target.value)} />
                    <button onClick={submitMarks}>Submit Marks</button>
                </>
            )}
        </div>
    );
}
export default AdminSubmission;
