import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function StudentExamResult() {
  const { studentId, examId } = useParams();
  const [exam, setExam] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [questions, setQuestions] = useState([]);
  const [marks, setMarks] = useState([]);


  useEffect(() => {
    const fetchExam = async () => {
      try {
        const response = await fetch(`http://localhost:5001/api/student-exam/${examId}`);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const examData = await response.json();
        setExam(examData);


        const answerPromises = examData.questionIds.map(async (questionId) => {
          const response = await fetch(`http://localhost:5001/api/student-view-answer/${studentId}/${questionId}`);
          if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
          }
          const answerData = await response.json();
          return answerData.answerText;
        });

        const questionPromises = examData.questionIds.map(async (questionId) => {
          const response = await fetch(`http://localhost:5001/api/student-question/${questionId}`);
          if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
          }
          const questionData = await response.json();
          return questionData.questionText;
        });

        const questions = await Promise.all(questionPromises);
        setQuestions(questions);
        const answers = await Promise.all(answerPromises);
        setAnswers(answers);
      } catch (error) {
        console.error(error);
      }
    };

    fetchExam();
  }, [examId, studentId]);
  return (
    <div>
      {exam && (
        <>
          <h2>Exam Details:</h2>
          <p>Name: {exam.name}</p>
          <p>Duration: {exam.duration}</p>
          <p>Total Marks: {exam.totalMarks}</p>
          <p>Subject: {exam.subject}</p>
          <p>Marks Obtained: {marks}</p>
          {exam.questionIds.map((id, index) => (
            <div key={index}>
              <p>Question: {questions[index]}</p>
              <p>Answer: {answers[index]}</p>
            </div>
          ))}
        </>
      )}
    </div>
  );
}
export default StudentExamResult;
